    <script src="https://cdn.jsdelivr.net/npm/gsap@3.13.0/dist/gsap.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/gsap@3.13.0/dist/ScrollTrigger.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/gsap@3.13.0/dist/ScrollSmoother.min.js"></script>
    <script>
        document.addEventListener("DOMContentLoaded", () => {
            gsap.registerPlugin(ScrollTrigger, ScrollSmoother);
            ScrollSmoother.create({
                wrapper: "#smooth-wrapper",
                content: "#smooth-content",
                smooth: 1,
                effects: true,
            });
        });

        var tm = gsap.timeline({
            defaults: {
                duration: 2,
                ease: "none"
            },
            scrollTrigger: {
                trigger: "#hero, #about",
                markers: false,
                scrub: true,
                start: "top top",
                end: "bottom top",
                pin: false,
            }

        });


        document.querySelectorAll('a[href^="#"]').forEach(anchor => {
            anchor.addEventListener('click', function(e) {
                e.preventDefault();
                const targetId = this.getAttribute('href');
                const targetElement = document.querySelector(targetId);

                if (targetElement) {
                    ScrollSmoother.get().scrollTo(targetElement, true, "start");
                }
            });
        });

        tm.to("#hero", {
            scale: 0.3
        });


        const projects = document.querySelector("#projects");

        function getScrollAmount() {
            let projectsWidth = projects.scrollWidth;
            return -(projectsWidth - window.innerWidth);
        }

        gsap.to("#projects", {
            x: () => getScrollAmount(),
            ease: "none",
            scrollTrigger: {
                trigger: "#projects",
                start: "top 0%",
                end: () => `+=${getScrollAmount() * -1}`,
                pin: true,
                scrub: 1,
                invalidateOnRefresh: true,
                markers: false
            }
        });


        var allowedKeys = {
            37: 'left',
            38: 'up',
            39: 'right',
            40: 'down',
            65: 'a',
            66: 'b'
        };

        var konamiCode = ['up', 'up', 'down', 'down', 'left', 'right', 'left', 'right', 'b', 'a'];

        var konamiCodePosition = 0;

        document.addEventListener('keydown', function(e) {
            var key = allowedKeys[e.keyCode];
            var requiredKey = konamiCode[konamiCodePosition];

            if (key == requiredKey) {

                konamiCodePosition++;

                if (konamiCodePosition == konamiCode.length) {
                    activateCheats();
                    konamiCodePosition = 0;
                }
            } else {
                konamiCodePosition = 0;
            }
        });

        function activateCheats() {
            document.body.style.backgroundImage = "url('tim.jpeg')";
            game = document.createElement('div');
            game.style.position = 'fixed';
            game.innerHTML =
                '<iframe frameborder="0" src="https://itch.io/embed-upload/6326063?color=76beff" allowfullscreen="" width="640" height="600"><a href="https://august7337.itch.io/jetpack">Play JetPack on itch.io</a></iframe>';
            document.body.appendChild(game);
        }
    </script>

